INSERT INTO `course_aim` VALUES
(NULL, NULL, $camp_id, 'Der Kurs ermöglicht den TN die Auseinandersetzung mit den Pfadigrundlagen.', NULL);
INSERT INTO `course_aim` VALUES
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN verfügen über vertiefte Kenntnisse der Pfadigrundlagen und können mit ihrer Hilfe die Ausgewogenheit des Pfadiprogramms sicherstellen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN verfügen über vertiefte Kenntnisse der Bedürfnisse der Kinder der Wolfsstufe und erkennen die unterschiedlichen Bedürfnisse von Mädchen und Jungen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen die im Pfadiprofil festgelegten Ziele der Wolfsstufe zu den fünf Pfadibeziehungen, sowie Möglichkeiten, diese zu erreichen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen den Bezug der Wolfsstufensymbolik zu den Pfadigrundlagen und wissen, wie sie im Programm umgesetzt werden kann.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen die Wichtigkeit von stillen Momenten zur Auseinandersetzung mit der Beziehung zum Spirituellen und können diese ins Programm der Wolfsstufe einbauen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN verfügen über vertiefte Kenntnisse der Pfadimethode „Persönlichen Fortschritt fördern“ und können diese längerfristig ins Programm der Wolfsstufe einbauen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen die Chancen und Herausforderungen der Integration von Menschen mit anderem kulturellen Hintergrund in die Pfadi.', NULL);
INSERT INTO `course_aim` VALUES
(NULL, NULL, $camp_id, 'Der Kurs bildet die TN zu verantwortlichen Einheitsleiter/-innen oder Stufenleiter/-innen aus.', NULL);
INSERT INTO `course_aim` VALUES
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen ihre Funktion sowie ihre Rechte und Pflichten als Einheitsleiter/-in oder Stufenleiter/-in der Wolfsstufe.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN wissen, wie sie ihre Einheit oder Stufe organisieren können und kennen Aspekte einer langfristigen Planung.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN können selbstbewusst auftreten und Anliegen vertreten.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen das kantonale Krisenkonzept und ihre Rolle darin.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen Möglichkeiten, um Elternkontakte bewusst zu pflegen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN können mit ihrem Leitungsteam Strategien zum Umgang mit Kindern mit herausforderndem Verhalten entwickeln und umsetzen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN sind sich der Suchtproblematik bewusst und kennen Möglichkeiten zur Prävention im Pfadiprogramm sowie Wege zum Umgang mit Suchtmitteln im Leitungsteam.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen ihre Verantwortung in der Prävention sexueller Ausbeutung und wissen, welche Massnahmen in diesem Bereich vorbeugend wirken.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen die unterschiedlichen Formen von Gewalt (psychische und physische), mit denen sie in ihrem Pfadialltag konfrontiert sein können sowie Möglichkeiten diesen vorzubeugen.', NULL);
INSERT INTO `course_aim` VALUES
(NULL, NULL, $camp_id, 'Der Kurs bildet die TN zu verantwortlichen Lagerleiter/-innen aus.', NULL);
INSERT INTO `course_aim` VALUES
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen ihre Funktion sowie ihre Rechte und Pflichten als Lagerleiter/-in.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen das Lagerreglement der PBS.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen die Funktionen und Aufgaben von Coach und AL, insbesondere bei der Lagerbetreuung.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen den Ablauf der Lagerplanung, verfügen über Werkzeuge für die einzelnen Planungsschritte und können Lager administrieren.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN können Lagerprogramme für die Wolfsstufe gestalten und kennen verschiedene Lagerformen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN können ein umfassendes Sicherheitskonzept für ein Lager erstellen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN können sicherheitsrelevante Aktivitäten planen sowie über Durchführung und Anpassungen vor, als auch während der Aktivitäten entscheiden.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN wissen, welche Massnahmen zur Gesundheit der Teilnehmer im Lager beitragen und kennen Möglichkeiten mit Krankheiten im Lager umzugehen.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN können bekannte Spiele abändern und grössere sportliche Aktivitäten organisieren.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN verfügen über vertiefte Kenntnisse der Planung, Durchführung und Auswertung von Wanderungen für die Wolfsstufe.', NULL);
INSERT INTO `course_aim` VALUES
(NULL, NULL, $camp_id, 'Der Kurs befähigt die TN, junge Leiter/-innen anzuleiten und zu betreuen.', NULL);
INSERT INTO `course_aim` VALUES
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen ihre Rolle als Teamleiter/-innen und kennen Rahmenbedingungen, die für eine erfolgreiche Zusammenarbeit im Team notwendig sind.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN können eine Sitzung vorbereiten und leiten.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN wissen, wie sie die einzelnen Teammitglieder betreuen und ihren persönlichen Fortschritt fördern können.', NULL),
(NULL, LAST_INSERT_ID(), $camp_id, 'Die TN kennen Möglichkeiten der Aus- und Weiterbildung.', NULL);
